import React, { Component } from "react";

export default class ListUsers extends Component {
  render() {
    console.log(this.props);
    return (
      <ol>
        {this.props.profiles.map((profile) => {
            const user = this.props.users.find((user) => user.id === profile.userID).name;
            const movie = this.props.movies.find((movie) => movie.id === profile.favoriteMovieID).name;
          return (
            <li key={profile.id}>
              `${user}'s favorite movie is ${movie}`
            </li>
          );
        })}
      </ol>
    );
  }
}
