import React, { Component } from 'react';
import ListContact from './ListContact';
import ListUsers from "./ListUsers";

// const contacts = [
//   {
//     id: "karen",
//     name: "Karen Isgrigg",
//     handle: "karen_isgrigg",
//     avatarURL: "http://localhost:5001/karen.jpg",
//   },
//   {
//     id: "richard",
//     name: "Richard Kalehoff",
//     handle: "richardkalehoff",
//     avatarURL: "http://localhost:5001/richard.jpg",
//   },
//   {
//     id: "tyler",
//     name: "Tyler McGinnis",
//     handle: "tylermcginnis",
//     avatarURL: "http://localhost:5001/tyler.jpg",
//   },
// ];

const profiles = [
  {
    id: 1,
    userID: "1",
    favoriteMovieID: "1",
  },
  {
    id: 2,
    userID: "2",
    favoriteMovieID: "1",
  },
  {
    id: 3,
    userID: "4",
    favoriteMovieID: "5",
  },
  {
    id: 4,
    userID: "5",
    favoriteMovieID: "2",
  },
  {
    id: 5,
    userID: "3",
    favoriteMovieID: "5",
  },
  {
    id: 6,
    userID: "6",
    favoriteMovieID: "4",
  },
];

const users = {
  1: {
    id: 1,
    name: "Jane Cruz",
    userName: "coder",
  },
  2: {
    id: 2,
    name: "Matthew Johnson",
    userName: "mpage",
  },
  3: {
    id: 3,
    name: "Autumn Green",
    userName: "user123",
  },
  4: {
    id: 4,
    name: "John Doe",
    userName: "user123",
  },
  5: {
    id: 5,
    name: "Lauren Carlson",
    userName: "user123",
  },
  6: {
    id: 6,
    name: "Nicholas Lain",
    userName: "user123",
  },
};

const movies = {
  1: {
    id: 1,
    name: "Planet Earth 1",
  },
  2: {
    id: 2,
    name: "Selma",
  },
  3: {
    id: 3,
    name: "Million Dollar Baby",
  },
  4: {
    id: 4,
    name: "Forrest Gump",
  },
  5: {
    id: 5,
    name: "Get Out",
  },
};

const UsersList = ({users, usersId}) => {
  return (
    <ul>
    {usersId.map((id) => <li>{users[id]}</li>)}
    </ul>
  )
} 

const MovieListWhoLiked = () => {
  let moviesList = [];
  for (const key in movies) {
    moviesList.push(movies[key]);
  }
  let moviesLiked = [];
  moviesList.map(( movie ) => {
    let idsUser = [];
    profiles.map( profile => {
      console.log(profile);
      if (profile.favoriteMovieID === movie.id && idsUser.indexOf(profile.userID) < 0) {
        console.log(profile, 'inside');
        idsUser.push(profile.userID);
      }
    });
    moviesLiked.push({
      idMovies: movie.id,
      movie: movie.name,
      users: idsUser
    });
  });
  return (
    <div>
      <ol>
        {moviesLiked.map( liked => {
          return <li>
              <h2>{liked.movie}</h2>
              <p>Liked By:</p>
              <UsersList users={users} usersId={liked.users}/>
            </li>;
        })}
      </ol>
    </div>
  )
}

class App extends Component {
  render() {
    console.log()
    return <div>
        {/* <ListContact contacts={contacts} /> */}
        <MovieListWhoLiked/>
        <ListUsers profiles={profiles} users={users} movies={movies} />
      </div>;
  }
}

export default App;
