import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from "react-router-dom";

export default class ListContact extends Component {
    render() {
        console.log(this.props);
        return <div>
            <div className="list-contact-top">
              <input type="text" placeholder="Say Something" onChange={(event) => this.changeText(event.target.value)} />
            <Link to='/create' className='add-contact'>
                Add contact
            </Link>
            </div>
            <ol>
              {this.props.contacts.map((contact) => {
                return <li key={contact.id}>
                    <div className="contact-avatar" style={{ backgroundImage: `url(${contact.avatarUrl})` }} />
                    <div className="contact-details">
                      <p>{contact.name}</p>
                      <p>{contact.handle}</p>
                    </div>
                    <button className="contact-remove" onClick={() => this.props.onDelete(contact)}>
                      Remove
                    </button>
                  </li>;
              })}
            </ol>
          </div>;
    }
}

ListContact.propTypes = {
  contacts: PropTypes.array.isRequired,
  onDelete: PropTypes.func.isRequired,
};
